class Solution {
public:
    bool isSubsequence(string s, string t) {
        int i=0;
        if(s.size()==0) return true;
        bool flag = false;
        for(char c: t){
            if(c==s[i]){
                i++;
            }
            if(i==s.size()){
                flag = true;
                break;
            }
        }
        return flag;
    }
};
```

